/*Table structure for table `tb_item` */

DROP TABLE IF EXISTS `tb_item`;

CREATE TABLE `tb_item` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '商品id，同时也是商品编号',
  `title` varchar(100) NOT NULL COMMENT '商品标题',
  `sell_point` varchar(500) DEFAULT NULL COMMENT '商品卖点',
  `price` decimal(20,2) NOT NULL COMMENT '商品价格，单位为：元',
  `num` int(10) NOT NULL COMMENT '库存数量',
  `barcode` varchar(30) DEFAULT NULL COMMENT '商品条形码',
  `image` varchar(2000) DEFAULT NULL COMMENT '商品图片',
  `categoryId` bigint(10) NOT NULL COMMENT '所属类目，叶子类目',
  `status` varchar(1) NOT NULL COMMENT '商品状态，1-正常，2-下架，3-删除',
  `create_time` datetime NOT NULL COMMENT '创建时间',
  `update_time` datetime NOT NULL COMMENT '更新时间',
  `cost_pirce` decimal(10,2) DEFAULT NULL COMMENT '现价',
  `market_price` decimal(10,2) DEFAULT NULL COMMENT '商城价',
  `is_default` varchar(1) DEFAULT NULL COMMENT '多个SKU时，是否是默认显示的SKU',
  `goods_id` bigint(20) DEFAULT NULL COMMENT 'SPUid',
  `seller_id` varchar(30) DEFAULT NULL COMMENT '商家id（用户名）',
  `cart_thumbnail` varchar(150) DEFAULT NULL COMMENT '缩略图',
  `category` varchar(200) DEFAULT NULL COMMENT '所属3级分类名称-冗余字段',
  `brand` varchar(100) DEFAULT NULL COMMENT '品牌名称-冗余字段',
  `spec` varchar(200) DEFAULT NULL COMMENT '规格',
  `seller` varchar(200) DEFAULT NULL COMMENT '店铺名称-冗余字段',
  PRIMARY KEY (`id`),
  KEY `cid` (`categoryId`),
  KEY `status` (`status`),
  KEY `updated` (`update_time`)
) ENGINE=InnoDB AUTO_INCREMENT=1369307 DEFAULT CHARSET=utf8 COMMENT='商品表';

